#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="$basedir\D:\HTML-study\code\Cesium-1.109\node_modules\.store\cloc@2.11.0\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="$basedir/D:/HTML-study/code/Cesium-1.109/node_modules/.store/cloc@2.11.0/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$env_node_path$pathsep$new_node_path"
}

$ret=0
if (Test-Path "$basedir/perl$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/perl$exe"  "$basedir/../.store/cloc@2.11.0/node_modules/cloc/lib/cloc" $args
  } else {
    & "$basedir/perl$exe"  "$basedir/../.store/cloc@2.11.0/node_modules/cloc/lib/cloc" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "perl$exe"  "$basedir/../.store/cloc@2.11.0/node_modules/cloc/lib/cloc" $args
  } else {
    & "perl$exe"  "$basedir/../.store/cloc@2.11.0/node_modules/cloc/lib/cloc" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
